import { useState } from "react";

export default function Blog() {
    const [formData, setFormData] = useState({ title: "", content: "" });
    const [blogs, setBlogs] = useState([]);

    function handleSubmit(e) {
        e.preventDefault();

        setBlogs([{ title: e.formData.title, content: e.formData.content }, ...blogs]);
        setFormData({ title: "", content: "" });
        console.log(blogs); // Note: This may still not reflect the updated state immediately after setBlogs due to state batching
    }

    function removeBlog(i) {
        setBlogs(blogs.filter((blog, index) => index !== i));
    }

    return (
        <>
            <h1>Write a Blog!</h1>
            <div className="section">
                {/* Form to write the blog */}
                <form onSubmit={handleSubmit}>
                    <Row label="Title">
                        <input
                            className="input"
                            placeholder="Enter the Title of the Blog here.."
                            value={formData.title}
                            onChange={(e) =>
                                setFormData((prevData) => ({
                                    ...prevData,
                                    title: e.target.value,
                                }))
                            }
                        />
                    </Row>

                    <Row label="Content">
                        <textarea
                            className="input content"
                            placeholder="Content of the Blog goes here.."
                            value={formData.content}
                            onChange={(e) =>
                                setFormData((prevData) => ({
                                    ...prevData,
                                    content: e.target.value,
                                }))
                            }
                        />
                    </Row>

                    <button className="btn">ADD</button>
                </form>
            </div>

            <hr />

            {/* Section where submitted blogs will be displayed */}
            <h2>Blogs</h2>
            {blogs.map((blog, i) => (
                <div className="blog" key={i}> {/* Added key for list rendering */}
                    <h3>{blog.title}</h3>
                    <hr />
                    <p>{blog.content}</p>

                    <div className="blog-btn">
                        <button
                            onClick={() => {
                                removeBlog(i);
                            }}
                            className="btn remove"
                        >
                            Delete
                        </button>
                    </div>
                </div>
            ))}
        </>
    );
}

// Row component to introduce a new row section in the form
function Row(props) {
    const { label } = props;
    return (
        <>
            <label>{label}<br /></label>
            {props.children}
            <hr />
        </>
    );
}
