import React from "react";

class MovieCard extends React.Component{

    constructor() {
        super();
        this.state = {
            title: "The Avengers",
            plot: "Earth's mightiest heroes must come together and learn to fight as a team if they are going to stop the mischievous Loki and his alien army from enslaving humanity.",
            price : 699,
            rating : 4.5,
            stars : 0,
            fav : false
        }
    }

    // addStars(){
    //     this.state.stars+=0.5;
    //     console.log("Current state : ", this.state);
    // }

    addStars=()=>{
         // form 1 of using setState
        //  this.setState({
        //     stars : this.state.stars+0.5
        //  });

        // form 2 for setting states 
        if(this.state.stars>=5){
            this.state.stars = 5;
            return;
        }
        this.setState((prevState) => {
            return {
                stars : prevState.stars+0.5
            }
        })


    }

    decStars=()=>{
        //form 1 of using setState
        if(this.state.stars<=0){
            this.state.stars = 0;
            return;
        }
        this.setState({
           stars : this.state.stars-0.5
        });
   }

   handleUnFav = () => {
      this.setState({
        fav : !this.state.fav
      })
   }

   handleFav = () => {
    this.setState({
      fav : !this.state.fav
    })
 }

    render(){
        const {title, plot, price, rating, stars, fav} = this.state;  
        return(
            <div className="main">

                {/**Movie Card */}
                <div className="movie-card">

                    {/**Left section of Movie Card */}
                    <div className="left">
                        <img alt="poster" src='./poster/the-avengers.jpg' />
                    </div>

                    {/**Right section Movie Card */}
                    <div className="right">

                        {/**Title, plot, price of the movie */}
                        <div className="title">{this.state.title}</div>
                        <div className="plot">{plot}</div>
                        <div className="price">Rs. {price}</div>

                        {/**Footer starts here with ratings, stars and buttons */}
                        <div className="footer">
                            <div className="rating">{rating}</div>

                            {/**Star image with increase and decrease buttons and star count */}
                            <div className="star-dis">
                                <img className="str-btn" 
                                    alt="Decrease" 
                                    src="https://cdn-icons-png.flaticon.com/128/2801/2801932.png"
                                    onClick={this.decStars} 
                                />
                                <img className="stars" 
                                        alt="stars" 
                                        src="https://cdn-icons-png.flaticon.com/128/2107/2107957.png"    
                                />
                                <img className="str-btn" 
                                    alt="increase" 
                                    src="https://cdn-icons-png.flaticon.com/128/2997/2997933.png" 
                                    onClick={this.addStars.bind(this)}
                                />
                                <span className="starCount">{stars}</span>
                            </div>

                            {/* {fav ? <button className="unfavourite-btn" onClick={this.handleUnFav}>un-Favourite</button> : 
                                  <button className="favourite-btn" onClick={this.handleFav}>Favourite</button>}  */}

<button className={fav?"favourite-btn":"unfavourite-btn"} onClick={this.handleUnFav}>{fav?"Favourite":"un-Favourite"}</button>




                            <button className="cart-btn">Add to Cart</button>

                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default MovieCard;
